import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-regular-svg-icons'
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

const Navbar = ({authenticate}) => {
  useState(()=>{
    goToLogin();
    console.log("aaa", authenticate)
  },[authenticate])
    const menuList = ['여성', 'Divided', "남성", "신생아/유아", "아동", "H$M Home", "Sale", "지속가능성",];
    const navigate = useNavigate();
    const goToLogin = () => {
      if (authenticate == false) {
        navigate("/login");
        console.log("다시 로그인됨");
      } else {
        navigate("/");
        console.log("로그아웃");
      }
    }
    const goToMain = () => {
      navigate("/")
    }
    const search = (event) => {
      //console.log("key press")
      if (event.key === "Enter") {
        console.log("user presses a key", event.key);
        // 입력한 검색어를 읽어온다.
        let keyword = event.target.value;
        console.log("keyword", keyword);
        // 읽어온 값으로 url을 바꿔준다.
        navigate(`/?q=${keyword}`) // 파라미터 값이 아닌 쿼리 값. (?q=검색어)
      }
    }
  return (
    <div>
      <div className="login-button" onClick={goToLogin}>
        <FontAwesomeIcon icon = {faUser}/>
        <div>로그인</div>
      </div>
      <div className="nav-section" onClick={goToMain}>
        <img width={100} src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/53/H%26M-Logo.svg/1200px-H%26M-Logo.svg.png"/>
      </div>
      <div className="menu-area">
        <ul className="menu-list">
            {menuList.map((menu, idx)=>(<li>{menu}</li>))}
        </ul>
        <div className="search-area">
            <FontAwesomeIcon icon={faSearch}/>
            <input type="text" onKeyPress={(event)=>search(event)}/>
        </div>
      </div>
    </div>
  )
}

export default Navbar
