import React, {useEffect, useState} from 'react'
import { Container, Row, Col } from 'react-bootstrap';
import { useParams } from 'react-router-dom'

const ProductDetail = () => {
  let{id} = useParams();
  const [product, setProduct] = useState(null) // 3. api data를 state에 저장!
  const getProductDetail = async() => {
    let url = ` http://localhost:4000/products/${id}` // 1. 이 id 숫자를 읽어서 json 서버에 보내준다. -> useParams
    let response = await fetch(url)
    let data = await response.json();
    console.log("ddd",data); // 2. data를 확인했으면 이제 UI에 보여주면 된다!
    setProduct(data);
  }
  useEffect(()=>{
    getProductDetail()
  },[])

  return (
    <Container>
      <Row>
        <Col className='product-img'>
          <img src={product?.img}/>
          {/* product 안에 img src가 담겨 있음! product가 있다면 img를 보여준다. */}
        </Col>
        <Col>
          <h5>{product?.title}</h5>
          <div>{product?.choice==true?"Conscious choice":""}</div>
          <h5>KRW{product?.price}</h5>
        </Col>
      </Row>
    </Container>
  )
}

export default ProductDetail
